import * as React from 'react'

import useColourValue from '@/utils/hooks/use-colour-value'
import type { ColourValues, TintValues } from '@/utils/hooks/use-colour-value'
import styles from '@/components/BrandAsset/BrandAsset.module.css'

type BrandAssets = ['Logo']

type Props = {
  type: BrandAssets[number]
  colour?: ColourValues
  tint?: TintValues
  width?: number
  height?: number
  title?: string
  ariaHidden?: boolean
  className?: string
  style?: React.CSSProperties
}

const BrandAsset = React.forwardRef((props: Props, ref: React.ForwardedRef<SVGSVGElement>) => {
  const { type, colour = 'neutral', tint, ...rest } = props
  const resolvedColour = useColourValue(colour, tint)

  switch (type) {
    case 'Logo':
      return <Logo ref={ref} colour={resolvedColour} {...rest} />
    default:
      return null
  }
})

BrandAsset.displayName = 'BrandAsset'

